// Combine app shell and assets into one list, ensuring no duplicates
const URLS_TO_CACHE = [...new Set([...APP_SHELL_URLS, ...ASSET_URLS])];

self.addEventListener('install', (event) => {
  self.skipWaiting(); // Force the new service worker to activate immediately
  event.waitUntil(
    caches.open(CACHE_NAME)
      .then((cache) => {
        console.log(`[Service Worker] Opened cache ${CACHE_NAME}. Caching ${URLS_TO_CACHE.length} assets for offline use.`);
        // Use addAll with individual requests to prevent one failure from stopping the whole cache
        const promises = URLS_TO_CACHE.map(url => {
          return fetch(url, { mode: 'cors' }).then(response => {
            if (!response.ok) {
               console.error(`[Service Worker] Failed to fetch ${url}: ${response.status} ${response.statusText}`);
               return Promise.resolve(); // Don't fail the entire install
            }
            return cache.put(url, response);
          }).catch(err => {
            console.error(`[Service Worker] Network error fetching ${url}:`, err);
          });
        });
        return Promise.all(promises);
      })
      .then(() => {
        console.log('[Service Worker] All available assets cached.');
      })
      .catch(err => {
        console.error('[Service Worker] Caching failed during install:', err);
      })
  );
});

self.addEventListener('activate', (event) => {
  const cacheWhitelist = [CACHE_NAME];
  event.waitUntil(
    caches.keys().then((cacheNames) => {
      return Promise.all(
        cacheNames.map((cacheName) => {
          if (cacheWhitelist.indexOf(cacheName) === -1) {
            console.log('[Service Worker] Deleting old cache:', cacheName);
            return caches.delete(cacheName);
          }
        })
      );
    }).then(() => self.clients.claim()) // Take control of all open clients
  );
});

self.addEventListener('fetch', (event) => {
  if (event.request.method !== 'GET') {
    return;
  }
  
  const requestUrl = new URL(event.request.url);

  // Don't cache API calls
  if (requestUrl.pathname.startsWith('/api/')) {
    return;
  }
  
  event.respondWith(
    caches.open(CACHE_NAME).then(async (cache) => {
      const urlWithoutQuery = requestUrl.origin + requestUrl.pathname;

      // 1. Try to find a match in the cache. Check request with query params first, then without.
      let cachedResponse = await cache.match(event.request);
      if (cachedResponse) {
        return cachedResponse;
      }
      cachedResponse = await cache.match(urlWithoutQuery);
       if (cachedResponse) {
        return cachedResponse;
      }

      // 2. If not in cache, fetch from the network.
      try {
        const networkResponse = await fetch(event.request);
        // If the fetch is successful, cache the response for future use.
        if (networkResponse && networkResponse.status === 200) {
          const responseToCache = networkResponse.clone();
          // Cache using the full request URL (with query params) to handle dynamic content,
          // but our lookup logic can still find it by the base URL if needed.
          cache.put(event.request, responseToCache);
        }
        return networkResponse;
      } catch (error) {
        // The fetch failed, likely due to being offline.
        // We've already checked the cache and didn't find it.
        console.log(`[Service Worker] Fetch failed for ${event.request.url}; returning network error.`, error);
        // Let the browser handle the error (e.g., show a broken image icon).
        return new Response('', { status: 408, statusText: 'Request timed out.'});
      }
    })
  );
});
